changeSet.sql:
CREATE TABLE IF NOT EXISTS Animals.AnimalType(
id           bigint not null primary key,
AnimalType   varchar(255)
);

CREATE TABLE IF NOT EXISTS Animals.Animal(
id           bigint not null primary key,
AnimalTypeId bigint,
Breed        varchar(255),
Name         varchar(255),
Cost         numeric(38, 2),
Character    varchar(255)
);
